interface Props {
  data: IProduct;
}

export const CartItem = (props: Props) => {
  const { _id, productName, price, productImage } = props.data;
  const { getCartItemCount, addToCart, removeFromCart, updateCartItemCount } =
    useContext(ShopContext);

